    fetch(`http://cgroup90@194.90.158.74/cgroup90/prod/api/put/eventapproval/${relatedEvent}`, {
      method: 'PUT',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(ansObj),
    })
